import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit, GrSuperscript, GrDown } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosSave } from 'react-icons/io';
import storeData from './LinkedList';
import logo from './style/logo.png';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];

    const [showDropdown, setShowDropdown] = useState(false);
    const toggleDropdown = () => setShowDropdown(!showDropdown);
    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [drawingEnabled, setDrawingEnabled] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const [editingText, setEditingText] = useState(false);
    const canvasRef = useRef(null);

    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    // Fetch image from local storage
    useEffect(() => {
        const imageDataUrl = localStorage.getItem('screenshotDataUrl');
        console.log(imageDataUrl);
        if (imageDataUrl) {
            const img = new Image();
            img.src = imageDataUrl;
            img.onload = () => {
                setDetails(img);
                setState(prevState => ({
                    ...prevState,
                    image: imageDataUrl
                }));
                storeData.insert({
                    image: imageDataUrl,
                    brightness: 100,
                    grayscale: 0,
                    sepia: 0,
                    saturate: 100,
                    contrast: 100,
                    hueRotate: 0,
                    rotate: 0,
                    vartical: 1,
                    horizental: 1
                });
            };
        }
    }, []);

    const handleShapeSelect = (shape) => {
        // Handle shape selection logic here
        console.log(`Selected shape: ${shape}`);
    };

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;
    
        // Adjust the canvas size to match the image size
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
    
        // Clear the canvas before drawing
        ctx.clearRect(0, 0, canvas.width, canvas.height);
    
        // Apply filters and transformations
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();
    
        // Draw the text if any
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    };
    
    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpeg');
        setState({
            ...state,
            image: base64Url
        });
    };

    const saveImage = () => {
        if (!details) return;
    
        // Create an offscreen canvas for final image processing
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
    
        // Set canvas dimensions to match the original image dimensions
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
    
        // Apply filters and transformations
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();
    
        // Draw the text if any
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    
        // Draw the current content of the main canvas (lines, etc.)
        const mainCanvas = canvasRef.current;
        if (mainCanvas) {
            ctx.drawImage(mainCanvas, 0, 0, canvas.width, canvas.height);
        }
    
        // Create a link to download the image
        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };
    
    const startDrawing = (e) => {
        if (drawingEnabled) {
            setDrawing(true);
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };

    const draw = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.strokeStyle = 'red'; // You can add a color picker here
            ctx.lineWidth = 2;
            ctx.stroke();
        }
    };

    const stopDrawing = () => {
        if (drawing) {
            setDrawing(false);
        }
    };

    const handleTextChange = (e) => {
        setText({ ...text, value: e.target.value });
    };

    const handleTextPositionChange = (e) => {
        const { name, value } = e.target;
        setText({ ...text, [name]: parseInt(value, 10) });
    };

    return (
        <div className='app-content'>
            <div className='sidebar'>
                <div className='sidebar-header'>
                    <img src={logo} alt='logo' />
                </div>
                <div className='sidebar-buttons'>
                    <button onClick={() => handleShapeSelect('rectangle')}>Rectangle</button>
                    <button onClick={() => handleShapeSelect('circle')}>Circle</button>
                    <button onClick={() => handleShapeSelect('line')}>Line</button>
                    <button onClick={() => handleShapeSelect('arrow')}>Arrow</button>
                    <button onClick={leftRotate}><GrRotateLeft /></button>
                    <button onClick={rightRotate}><GrRotateRight /></button>
                    <button onClick={varticalFlip}><CgMergeVertical /></button>
                    <button onClick={horizentalFlip}><CgMergeHorizontal /></button>
                    <button onClick={undo}><IoMdUndo /></button>
                    <button onClick={redo}><IoMdRedo /></button>
                    <button onClick={saveImage}><IoIosSave /></button>
                </div>
                <div className='filter-section'>
                    <div className='filter-select'>
                        <button onClick={toggleDropdown}>
                            {property.name}
                        </button>
                        {showDropdown && (
                            <ul className='dropdown-menu'>
                                {filterElement.map((filter, index) => (
                                    <li key={index} onClick={() => setProperty(filter)}>
                                        {filter.name}
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                    <input
                        type='range'
                        min='0'
                        max={property.maxValue}
                        value={state[property.name]}
                        onChange={inputHandle}
                        name={property.name}
                    />
                </div>
                <div className='drawing-tools'>
                    <button onClick={() => setDrawingEnabled(!drawingEnabled)}>Draw</button>
                </div>
                <div className='text-tools'>
                    <input
                        type='text'
                        value={text.value}
                        onChange={handleTextChange}
                        placeholder='Enter text'
                    />
                    <input
                        type='number'
                        name='x'
                        value={text.x}
                        onChange={handleTextPositionChange}
                        placeholder='X position'
                    />
                    <input
                        type='number'
                        name='y'
                        value={text.y}
                        onChange={handleTextPositionChange}
                        placeholder='Y position'
                    />
                </div>
            </div>
            <div className='image-container'>
                <canvas
                    ref={canvasRef}
                    width='800'
                    height='600'
                    onMouseDown={startDrawing}
                    onMouseMove={draw}
                    onMouseUp={stopDrawing}
                />
                {state.image && <img src={state.image} alt='edit' />}
            </div>
        </div>
    );
};

export default Main;
