import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit, GrSuperscript } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosImage } from 'react-icons/io';
import { Stage, Layer, Image, Text } from 'react-konva';
import Konva from 'konva';
import storeData from './LinkedList';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];

    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [texts, setTexts] = useState([]);
    const [isAddingText, setIsAddingText] = useState(false);
    const [selectedText, setSelectedText] = useState(null);
    const canvasRef = useRef(null);

    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop]);

    const addText = () => {
        setIsAddingText(true);
    };
    
    const handleTextClick = (e) => {
        const text = e.target;
        setSelectedText(text);
    };
    
    const handleStageClick = (e) => {
        if (isAddingText) {
            const { x, y } = e.target.getStage().getPointerPosition();
            setTexts([...texts, { x, y, text: 'Edit me', id: `text_${texts.length + 1}` }]);
            setIsAddingText(false);
        } else {
            setSelectedText(null);
        }
    };

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;

        const { width, height } = canvas;
        ctx.clearRect(0, 0, width, height);

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(width / 2, height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -width / 2, -height / 2, width, height);
    };

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const handleTextChange = (e) => {
        const newText = e.target.value;
        setTexts(texts.map(t => t.id === selectedText.id ? { ...t, text: newText } : t));
    };
    
    useEffect(() => {
        if (selectedText) {
            const textNode = stageRef.current.findOne(`#${selectedText.id}`);
            textNode.focus();
        }
    }, [selectedText]);
    
    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpeg');
        setState({
            ...state,
            image: base64Url
        });
    };

    const saveImage = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        if (!details) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );

        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    const startDrawing = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };

    const draw = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.strokeStyle = '#000000'; // Set the pencil color (black)
            ctx.lineWidth = 2; // Set the pencil width
            ctx.lineCap = 'round'; // Set the line cap for smoother lines
            ctx.stroke();
        }
    };

    const stopDrawing = () => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.closePath();
        }
    };

    const textAdd = () => {

    }

    const enableDrawing = () => {
        setDrawing(!drawing);
    };


    return (
        <div className='image_editor'>
            <div className="card">
                <div className="card_header">
                    <h2>------ Image Editor ------</h2>
                </div>
                <div className="card_body">
                    <div className="sidebar">
                        <div className="side_body">
                            <div className="filter_section">
                                <span>Filters</span>
                                <div className="filter_key">
                                    {filterElement.map((v, i) => (
                                        <button
                                            className={property.name === v.name ? 'active' : ''}
                                            onClick={() => setProperty(v)}
                                            key={i}
                                        >
                                            {v.name}
                                        </button>
                                    ))}
                                </div>
                            </div>
                            <div className="filter_slider">
                                <div className="label_bar">
                                    <label htmlFor="range">{property.name.charAt(0).toUpperCase() + property.name.slice(1)}</label>
                                    <span>{state[property.name]}</span>
                                </div>
                                <input
                                    name={property.name}
                                    onChange={inputHandle}
                                    value={state[property.name]}
                                    max={property.maxValue}
                                    type="range"
                                />
                            </div>
                            <div className="rotate">
                                <label htmlFor="">Rotate & Flip</label>
                                <div className="icon">
                                    <div onClick={leftRotate}><GrRotateLeft /></div>
                                    <div onClick={rightRotate}><GrRotateRight /></div>
                                    <div onClick={varticalFlip}><CgMergeVertical /></div>
                                    <div onClick={horizentalFlip}><CgMergeHorizontal /></div>
                                </div>
                                <div className="icon">
                                    <div onClick={enableDrawing}><GrEdit /></div>
                                    <div onClick={textAdd}><GrSuperscript /></div>
                                </div>
                            </div>

                        </div>
                        <div className="reset">
                            <button onClick={() => setState({
                                image: '',
                                brightness: 100,
                                grayscale: 0,
                                sepia: 0,
                                saturate: 100,
                                contrast: 100,
                                hueRotate: 0,
                                rotate: 0,
                                vartical: 1,
                                horizental: 1
                            })}>Reset</button>
                            <button onClick={saveImage} className='save'>Save Image</button>
                        </div>
                    </div>
                    <div className="image_section">
                        <div className="image">
                            {state.image ? (
                                <div style={{ position: 'relative' }}>
                                    <canvas
                                        ref={canvasRef}
                                        onMouseDown={startDrawing}
                                        onMouseMove={draw}
                                        onMouseUp={stopDrawing}
                                        className={drawing ? 'drawing-mode' : ''}
                                        style={{
                                            filter: `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`,
                                            transform: `rotate(${state.rotate}deg) scale(${state.vartical}, ${state.horizental})`,
                                            top: 0,
                                            left: 0
                                        }}
                                    />
                                    <img
                                        ref={(img) => setDetails(img)}
                                        src={state.image}
                                        alt=""
                                        style={{ display: 'none' }}
                                    />
                                </div>
                            ) : (
                                <label htmlFor="choose">
                                    <IoIosImage />
                                    <span>Choose Image</span>
                                </label>
                            )}
                        </div>
                        <div className="image_select">
                            <button onClick={undo} className='undo'><IoMdUndo /></button>
                            <button onClick={redo} className='redo'><IoMdRedo /></button>
                            {crop && <button onClick={imageCrop} className='crop'>Crop Image</button>}
                            <label htmlFor="choose">Choose Image</label>
                            <input onChange={imageHandle} type="file" id='choose' />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
