import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit, GrSuperscript } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosSave, IoIosImage } from 'react-icons/io';
import storeData from './LinkedList';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];
    
    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const [editingText, setEditingText] = useState(false);
    const canvasRef = useRef(null);
    
    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;
        
        const { width, height } = canvas;
        ctx.clearRect(0, 0, width, height);

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(width / 2, height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -width / 2, -height / 2, width, height);
        ctx.resetTransform();
        
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    };

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({...state, rotate: state.rotate - 90});
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({...state, rotate: state.rotate + 90});
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({...state, vartical: state.vartical === 1 ? -1 : 1});
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({...state, horizental: state.horizental === 1 ? -1 : 1});
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;
        
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpeg');
        setState({
            ...state,
            image: base64Url
        });
    };

    const saveImage = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        if (!details) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
        
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );
        ctx.resetTransform();
        
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }

        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    const startDrawing = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };
    
    const draw = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.strokeStyle = '#000000'; // Set the pencil color (black)
            ctx.lineWidth = 2; // Set the pencil width
            ctx.lineCap = 'round'; // Set the line cap for smoother lines
            ctx.stroke();
        }
    };
    
    const stopDrawing = () => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.closePath();
        }
    };

    const enableDrawing = () => {
        setDrawing(!drawing);
    };    

    const textAdd = () => {
        setText({ value: 'Hello World', x: 50, y: 50 });
        setEditingText(true);
    };

    const handleTextChange = (e) => {
        setText({ ...text, value: e.target.value });
    };

    const handleTextDrag = (e) => {
        if (editingText) {
            setText({ ...text, x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY });
        }
    };

    const stopEditingText = () => {
        setEditingText(false);
    };

    return (
        <div className="image-editor">
            <div className="header">
                <div className="header-buttons">
                    <button onClick={handleUndo}><IoMdUndo />Undo</button>
                    <button onClick={handleRedo}><IoMdRedo />Redo</button>
                    <button onClick={handleSaveImage}><IoIosSave />Save</button>
                </div>
            </div>
            <div className="main-content">
                <div className="sidebar">
                    <h2>Tools</h2>
                    <div className="sidebar-buttons">
                        <label className="image-upload">
                            <IoIosImage /> Upload Image
                            <input type="file" accept="image/*" onChange={handleImageChange} />
                        </label>
                        <button onClick={handleAddText}><GrEdit />Add Text</button>
                        <button><GrRotateLeft />Rotate Left</button>
                        <button><GrRotateRight />Rotate Right</button>
                        <button><CgMergeVertical />Crop</button>
                        <button><CgMergeHorizontal />Adjust</button>
                    </div>
                    <div className="adjustment-sliders">
                        <h3>Adjustments</h3>
                        <div className="slider">
                            <label>Brightness</label>
                            <input type="range" min="0" max="100" />
                        </div>
                        <div className="slider">
                            <label>Contrast</label>
                            <input type="range" min="0" max="100" />
                        </div>
                    </div>
                </div>
                <div className="canvas-container">
                    {imageSrc && (
                        <ReactCrop
                            src={imageSrc}
                            crop={crop}
                            onChange={handleCropChange}
                            onComplete={handleCropComplete}
                            onImageLoaded={onImageLoad}
                        />
                    )}
                    <canvas ref={canvasRef}></canvas>
                    {text && (
                        <input
                            type="text"
                            value={text}
                            style={{
                                position: 'absolute',
                                top: textPosition.y,
                                left: textPosition.x,
                                color: 'red',
                                fontSize: '30px',
                                background: 'transparent',
                                border: 'none',
                                outline: 'none'
                            }}
                            onChange={(e) => setText(e.target.value)}
                        />
                    )}
                </div>
            </div>
        </div>
    );
};

export default Main;
