import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit, GrSuperscript } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosImage } from 'react-icons/io';
import storeData from './LinkedList';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];
    
    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const [editingText, setEditingText] = useState(false);
    const [screenshot, setScreenshot] = useState(null);

    const canvasRef = useRef(null);
    
    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    useEffect(() => {
        fetch('http://localhost:3000/screenshot')
            .then(response => response.json())
            .then(data => setScreenshot(data.dataUrl))
            .catch(error => console.error('Error:', error));
    }, []);
    
    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;
        
        const { width, height } = canvas;
        ctx.clearRect(0, 0, width, height);

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(width / 2, height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
    };

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({...state, rotate: state.rotate - 90});
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({...state, rotate: state.rotate + 90});
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({...state, vartical: state.vartical === 1 ? -1 : 1});
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({...state, horizental: state.horizental === 1 ? -1 : 1});
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;
        
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpeg');
        setState({
            ...state,
            image: base64Url
        });
    };

    const saveImage = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        if (!details) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
        
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );

        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    const startDrawing = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };
    
    const draw = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.strokeStyle = '#000000'; // Set the pencil color (black)
            ctx.lineWidth = 2; // Set the pencil width
            ctx.lineCap = 'round'; // Set the line cap for smoother lines
            ctx.stroke();
        }
    };
    
    const stopDrawing = () => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.closePath();
        }
    };
    
    const enableDrawing = () => {
        setDrawing(!drawing);
    };    
    
    const textAdd = () => {
        setText({ value: 'Hello World', x: 50, y: 50 });
        setEditingText(true);
    };

    useEffect(() => {
        console.log(text.value);
    }, [text]);

    return (
        <div className='image_editor'>
            <div className="card">
                <div className="card_header">
                    <h2>------ Image Editor ------</h2>
                </div>
                <div className="card_body">
                    <div className="sidebar">
                        <div className="side_body">
                            <div className="filter_section">
                                <span>Filters</span>
                                <div className="filter_key">
                                    {filterElement.map((v, i) => (
                                        <button
                                            className={v.name === property.name ? 'active' : ''}
                                            onClick={() => setProperty(v)}
                                            key={i}>
                                            {v.name}
                                        </button>
                                    ))}
                                </div>
                                <div className="filter_value">
                                    <input
                                        type='range'
                                        max={property.maxValue}
                                        value={state[property.name]}
                                        onChange={inputHandle}
                                        name={property.name}
                                    />
                                </div>
                            </div>
                            <div className="rotate_section">
                                <span>Rotate</span>
                                <div className="rotate_key">
                                    <button onClick={leftRotate}>
                                        <GrRotateLeft />
                                    </button>
                                    <button onClick={rightRotate}>
                                        <GrRotateRight />
                                    </button>
                                </div>
                            </div>
                            <div className="rotate_section">
                                <span>Flip</span>
                                <div className="rotate_key">
                                    <button onClick={varticalFlip}>
                                        <CgMergeVertical />
                                    </button>
                                    <button onClick={horizentalFlip}>
                                        <CgMergeHorizontal />
                                    </button>
                                </div>
                            </div>
                            <div className="crop_image">
                                <span>Crop</span>
                                <div className="crop">
                                    <ReactCrop
                                        src={state.image}
                                        onImageLoaded={setDetails}
                                        crop={crop}
                                        onChange={newCrop => setCrop(newCrop)}
                                    />
                                    <button onClick={imageCrop}>Crop</button>
                                </div>
                            </div>
                            <div className="filter_section">
                                <span>Editing</span>
                                <div className="filter_key">
                                    <button onClick={enableDrawing}>
                                        <GrEdit />
                                    </button>
                                    <button onClick={textAdd}>
                                        <GrSuperscript />
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="image_section">
                        <div className="image">
                            <input type='file' onChange={imageHandle} />
                            <div>
                                <canvas
                                    ref={canvasRef}
                                    width={700}
                                    height={500}
                                    onMouseDown={startDrawing}
                                    onMouseMove={draw}
                                    onMouseUp={stopDrawing}
                                    onMouseLeave={stopDrawing}
                                >
                                    {screenshot && (
                                        <img
                                            src={screenshot}
                                            alt="Screenshot"
                                            style={{ display: 'none' }}
                                            onLoad={(e) => {
                                                const img = e.target;
                                                setDetails(img);
                                            }}
                                        />
                                    )}
                                </canvas>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card_footer">
                    <button onClick={undo}>
                        <IoMdUndo />
                    </button>
                    <button onClick={redo}>
                        <IoMdRedo />
                    </button>
                    <button onClick={saveImage}>
                        <IoIosImage />
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Main;
