import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import { GrRotateLeft, GrRotateRight, GrEdit } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosSave } from 'react-icons/io';
import storeData from './LinkedList';

const Main = () => {
    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vertical: 1,
        horizontal: 1
    });
    const [details, setDetails] = useState(null);
    const [drawing, setDrawing] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const canvasRef = useRef(null);

    useEffect(() => {
        drawImage();
    }, [state, details, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;

        const { width, height } = canvas;
        ctx.clearRect(0, 0, width, height);

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(width / 2, height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vertical, state.horizontal);
        ctx.drawImage(details, -width / 2, -height / 2, width, height);
        ctx.resetTransform();

        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const verticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vertical: prevState.vertical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vertical: prevState.vertical === 1 ? -1 : 1 });
    };

    const horizontalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizontal: prevState.horizontal === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizontal: prevState.horizontal === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState(prevState => ({
                        ...prevState,
                        image: reader.result
                    }));
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vertical: 1,
                        horizontal: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const saveImage = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        if (!details) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vertical, state.horizontal);
        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );
        ctx.resetTransform();

        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }

        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    const startDrawing = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };

    const draw = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.strokeStyle = '#000000'; // Set the pencil color (black)
            ctx.lineWidth = 2; // Set the pencil width
            ctx.lineCap = 'round'; // Set the line cap for smoother lines
            ctx.stroke();
        }
    };

    const stopDrawing = () => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.closePath();
        }
    };

    const enableDrawing = () => {
        setDrawing(!drawing);
    };

    const textAdd = () => {
        setText({ value: 'Hello World', x: 50, y: 50 });
    };

    const handleTextDrag = (e) => {
        if (text.value) {
            setText(prevText => ({ ...prevText, x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY }));
        }
    };

    return (
        <div className="main">
            <header className="header">
                <button onClick={saveImage} title="Save Image"><IoIosSave /></button>
                <button onClick={undo} title="Undo"><IoMdUndo /></button>
                <button onClick={redo} title="Redo"><IoMdRedo /></button>
            </header>
            <div className="content">
                <aside className="sidebar">
                    <input type="file" onChange={imageHandle} accept="image/*" />
                    <div className="controls">
                        <button onClick={leftRotate} title="Rotate Left"><GrRotateLeft /></button>
                        <button onClick={rightRotate} title="Rotate Right"><GrRotateRight /></button>
                        <button onClick={verticalFlip} title="Flip Vertical"><CgMergeVertical /></button>
                        <button onClick={horizontalFlip} title="Flip Horizontal"><CgMergeHorizontal /></button>
                        <button onClick={enableDrawing} title="Draw"><GrEdit /></button>
                        <button onClick={textAdd} title="Add Text">T</button>
                    </div>
                </aside>
                <main className="canvas-container">
                    <canvas
                        ref={canvasRef}
                        width="800"
                        height="600"
                        onMouseDown={startDrawing}
                        onMouseMove={draw}
                        onMouseUp={stopDrawing}
                        onMouseOut={stopDrawing}
                        onClick={handleTextDrag}
                    />
                </main>
            </div>
        </div>
    );
};

export default Main;
