import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import { GrRotateLeft, GrRotateRight, GrEdit, GrDown } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoIosSave } from 'react-icons/io';
import storeData from './LinkedList';
import logo from './style/logo.png';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];

    const [showDropdown, setShowDropdown] = useState(false);
    const toggleDropdown = () => setShowDropdown(!showDropdown);
    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const canvasRef = useRef(null);

    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    useEffect(() => {
        const storedImage = localStorage.getItem('screenshotUrl');
        if (storedImage) {
            const img = new Image();
            img.src = storedImage;
            img.onload = () => {
                setDetails(img);
                setState((prevState) => ({
                    ...prevState,
                    image: storedImage
                }));
                storeData.insert({
                    image: storedImage,
                    brightness: 100,
                    grayscale: 0,
                    sepia: 0,
                    saturate: 100,
                    contrast: 100,
                    hueRotate: 0,
                    rotate: 0,
                    vartical: 1,
                    horizental: 1
                });
            };
        }
    }, []);

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;

        // Adjust the canvas size to match the image size
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        // Clear the canvas before drawing
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Apply filters and transformations
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();

        // Draw the text if any
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const addText = () => {
        setText({ value: 'Hello World', x: 50, y: 50 });
    };

    const saveImage = () => {
        const canvas = canvasRef.current;
        const dataUrl = canvas.toDataURL();
        const link = document.createElement('a');
        link.href = dataUrl;
        link.download = 'edited-image.png';
        link.click();
    };

    return (
        <div className="main-content">
            <header className="header">
                <img src={logo} alt="Logo" className="logo" />
                <div className="header-right">
                    <button className="icon-button" onClick={saveImage}>
                        <IoIosSave className="icon" />
                    </button>
                </div>
            </header>
            <div className="main-container">
                <aside className="sidebar">
                    <div className="sidebar-content">
                        <input type="file" className="inputImage" onChange={(e) => {/* handle image upload */}} />
                        <div className={`dropdown ${showDropdown ? 'show-dropdown' : ''}`}>
                            <button className="dropbtn" onClick={toggleDropdown}>
                                {property.name} <GrDown />
                            </button>
                            <div className="dropdown-content">
                                {filterElement.map((filter) => (
                                    <button key={filter.name} onClick={() => setProperty(filter)}>
                                        {filter.name}
                                    </button>
                                ))}
                            </div>
                        </div>
                        <button onClick={leftRotate}><GrRotateLeft /></button>
                        <button onClick={rightRotate}><GrRotateRight /></button>
                        <button onClick={varticalFlip}><CgMergeVertical /></button>
                        <button onClick={horizentalFlip}><CgMergeHorizontal /></button>
                        <button onClick={addText}><GrEdit /></button>
                    </div>
                </aside>
                <main className="canvas-container">
                    <canvas ref={canvasRef} className="canvas"></canvas>
                </main>
            </div>
        </div>
    );
};

export default Main;
