    const [drawing, setDrawing] = useState(false);
    const [drawingEnabled, setDrawingEnabled] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const [editingText, setEditingText] = useState(false);
    const canvasRef = useRef(null);

    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    const handleShapeSelect = (shape) => {
        console.log(`Selected shape: ${shape}`);
    };

    // useEffect(() => {
    //     chrome.storage.local.get("screenshotDataUrl", (result) => {
    //         if (result.screenshotDataUrl) {
    //             const img = new Image();
    //             img.src = result.screenshotDataUrl;
    //             img.onload = () => {
    //                 setDetails(img);
    //                 setState((prevState) => ({
    //                     ...prevState,
    //                     image: result.screenshotDataUrl
    //                 }));
    //                 storeData.insert({
    //                     image: result.screenshotDataUrl,
    //                     brightness: 100,
    //                     grayscale: 0,
    //                     sepia: 0,
    //                     saturate: 100,
    //                     contrast: 100,
    //                     hueRotate: 0,
    //                     rotate: 0,
    //                     vartical: 1,
    //                     horizental: 1
    //                 });
    //             };
    //         }
    //     });
    // }, []);
    

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        ctx.clearRect(0, 0, canvas.width, canvas.height);

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();

        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    };

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpeg');
        setState({
            ...state,
            image: base64Url
        });
    };

    const saveImage = () => {
        if (!details) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();

        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }

        const link = document.createElement('a');
        link.download = 'image.png';
        link.href = canvas.toDataURL();
        link.click();
    };

    const handleMouseDown = (e) => {
        if (drawingEnabled) {
            setDrawing(true);
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };

    const handleMouseMove = (e) => {
        if (drawing && drawingEnabled) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.stroke();
        }
    };

    const handleMouseUp = () => {
        if (drawing && drawingEnabled) {
            setDrawing(false);
            storeData.insert(state);
        }
    };

    const enableDrawing = () => {
        setDrawingEnabled(!drawingEnabled);
    };

    const handleTextChange = (e) => {
        setText({
            ...text,
            value: e.target.value
        });
    };

    const handleTextMouseDown = (e) => {
        setEditingText(true);
    };

    const handleTextMouseMove = (e) => {
        if (editingText) {
            setText({
                ...text,
                x: e.nativeEvent.offsetX,
                y: e.nativeEvent.offsetY
            });
        }
    };

    const handleTextMouseUp = () => {
        setEditingText(false);
        storeData.insert(state);
    };

    const handleTextClick = () => {
        setText({
            ...text,
            value: ''
        });
    };

    const handleReset = () => {
        setState({
            ...state,
            brightness: 100,
            grayscale: 0,
            sepia: 0,
            saturate: 100,
            contrast: 100,
            hueRotate: 0,
            rotate: 0,
            vartical: 1,
            horizental: 1
        });
        setCrop({});
        setText({ value: '', x: 50, y: 50 });
        setDrawingEnabled(false);
    };

    return (
        <div className="app">
            <header>
                <div className="header-content">
                    <img src={logo} alt="Logo" />
                    <button className="save" onClick={saveImage}>
                        <IoIosSave size={20} />
                    </button>
                </div>
            </header>
            <div className="main-content">
                <aside>
                    <div className="filter-controls">
                        <div className="filter-dropdown">
                            <button className="filter-btn" onClick={toggleDropdown}>
                                {property.name} <GrDown size={10} />
                            </button>
                            {showDropdown && (
                                <div className="filter-dropdown-content">
                                    {filterElement.map((elem, index) => (
                                        <button
                                            key={index}
                                            onClick={() => {
                                                setProperty(elem);
                                                toggleDropdown();
                                            }}
                                        >
                                            {elem.name}
                                        </button>
                                    ))}
                                </div>
                            )}
                        </div>
                        <input
                            type="range"
                            min="0"
                            max={property.maxValue}
                            value={state[property.name]}
                            name={property.name}
                            onChange={inputHandle}
                        />
                    </div>
                    <div className="shape-controls">
                        <div className="shape-dropdown">
                            <button className="shape-btn" onClick={toggleShapeDropdown}>
                                {shapeProperty.name} <GrDown size={10} />
                            </button>
                            {showShapeDropdown && (
                                <div className="shape-dropdown-content">
                                    {shape.map((elem, index) => (
                                        <button
                                            key={index}
                                            onClick={() => {
                                                setShapeProperty(elem);
                                                toggleShapeDropdown();
                                            }}
                                        >
                                            {elem.name}
                                        </button>
                                    ))}
                                </div>
                            )}
                        </div>
                        <input
                            type="range"
                            min="0"
                            max={shapeProperty.maxValue}
                            value={state[shapeProperty.name]}
                            name={shapeProperty.name}
                            onChange={inputHandle}
                        />
                    </div>
                    <div className="rotate-controls">
                        <button onClick={leftRotate}>
                            <GrRotateLeft size={20} />
                        </button>
                        <button onClick={rightRotate}>
                            <GrRotateRight size={20} />
                        </button>
                    </div>
                    <div className="flip-controls">
                        <button onClick={varticalFlip}>
                            <CgMergeVertical size={20} />
                        </button>
                        <button onClick={horizentalFlip}>
                            <CgMergeHorizontal size={20} />
                        </button>
                    </div>
                    <div className="history-controls">
                        <button onClick={undo}>
                            <IoMdUndo size={20} />
                        </button>
                        <button onClick={redo}>
                            <IoMdRedo size={20} />
                        </button>
                    </div>
                    <div className="image-controls">
                        <input type="file" accept="image/*" onChange={imageHandle} />
                        <button className="crop" onClick={imageCrop}>
                            <IoIosImage size={20} />
                        </button>
                        <button className="reset" onClick={handleReset}>
                            <IoIosImage size={20} />
                        </button>
                    </div>
                </aside>
                <div className="app-content">
                    <div className="image-container">
                        {details && (
                            <canvas
                                ref={canvasRef}
                                onMouseDown={handleMouseDown}
                                onMouseMove={handleMouseMove}
                                onMouseUp={handleMouseUp}
                                className="image-canvas"
                            />
                        )}
                        {drawingEnabled && <div className="drawing-overlay" />}
                    </div>
                    <div
                        className="text-input"
                        style={{
                            position: 'absolute',
                            top: `${text.y}px`,
                            left: `${text.x}px`,
                        }}
                        onMouseDown={handleTextMouseDown}
                        onMouseMove={handleTextMouseMove}
                        onMouseUp={handleTextMouseUp}
                    >
                        <input
                            type="text"
                            value={text.value}
                            onChange={handleTextChange}
                            onClick={handleTextClick}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
