import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit, GrSuperscript, GrDown } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosSave, IoIosImage } from 'react-icons/io';
import storeData from './LinkedList';
import logo from './style/logo.png';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];

    const [showDropdown, setShowDropdown] = useState(false);
    const toggleDropdown = () => setShowDropdown(!showDropdown);
    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [drawingEnabled, setDrawingEnabled] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const [editingText, setEditingText] = useState(false);
    const canvasRef = useRef(null);

    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    const handleShapeSelect = (shape) => {
        // Handle shape selection logic here
        console.log(`Selected shape: ${shape}`);
    };

    useEffect(() => {
        const storedImage = localStorage.getItem('screenshotUrl');
        if (storedImage) {
            const img = new Image();
            img.src = storedImage;
            img.onload = () => {
                setDetails(img);
                setState((prevState) => ({
                    ...prevState,
                    image: storedImage
                }));
                storeData.insert({
                    image: storedImage,
                    brightness: 100,
                    grayscale: 0,
                    sepia: 0,
                    saturate: 100,
                    contrast: 100,
                    hueRotate: 0,
                    rotate: 0,
                    vartical: 1,
                    horizental: 1
                });
            };
        }
    }, []);

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;

        // Adjust the canvas size to match the image size
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        // Clear the canvas before drawing
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // Apply filters and transformations
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();

        // Draw the text if any
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    };

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpeg');
        setState({
            ...state,
            image: base64Url
        });
    };

    const saveImage = () => {
        if (!details) return;

        // Create an offscreen canvas for final image processing
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        // Set canvas dimensions to match the original image dimensions
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        // Apply filters and transformations
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();

        // Draw the text if any
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }

        // Draw the current content of the main canvas (lines, etc.)
        const mainCanvas = canvasRef.current;
        if (mainCanvas) {
            ctx.drawImage(mainCanvas, 0, 0, canvas.width, canvas.height);
        }

        // Create a link to download the image
        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    const handleMouseDown = (e) => {
        if (!drawingEnabled) return;
        setDrawing(true);
    };

    const handleMouseUp = () => {
        setDrawing(false);
    };

    const handleMouseMove = (e) => {
        if (!drawing || !drawingEnabled || !canvasRef.current) return;

        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.strokeStyle = 'blue';
        ctx.lineWidth = 5;
        ctx.lineCap = 'round';

        const rect = canvas.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;

        ctx.lineTo(x, y);
        ctx.stroke();
        ctx.beginPath();
        ctx.moveTo(x, y);
    };

    const handleTextChange = (e) => {
        setText({ ...text, value: e.target.value });
    };

    const handleTextMouseDown = (e) => {
        if (!editingText) return;
        const canvas = canvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const x = e.clientX - rect.left;
        const y = e.clientY - rect.top;
        setText({ ...text, x, y });
    };

    return (
        <div className="main-content">
            <header className="header">
                <img src={logo} alt="Logo" className="logo" />
                <div className="dropdown">
                    <button className="dropbtn" onClick={toggleDropdown}>
                        Filters
                    </button>
                    {showDropdown && (
                        <div className="dropdown-content">
                            {filterElement.map((v, i) => (
                                <button key={i} onClick={() => setProperty(v)}>
                                    {v.name}
                                </button>
                            ))}
                        </div>
                    )}
                </div>
                <div className="slider">
                    <label>{property.name}</label>
                    <input
                        type="range"
                        name={property.name}
                        max={property.maxValue}
                        value={state[property.name]}
                        onChange={inputHandle}
                    />
                </div>
            </header>
            <div className="app-content">
                <div className="sidebar">
                    <div className="sidebar-content">
                        <div className="icon-container">
                            <input type="file" accept="image/*" onChange={imageHandle} />
                            <IoIosImage className="icon" />
                        </div>
                        <div className="icon-container" onClick={leftRotate}>
                            <GrRotateLeft className="icon" />
                        </div>
                        <div className="icon-container" onClick={rightRotate}>
                            <GrRotateRight className="icon" />
                        </div>
                        <div className="icon-container" onClick={varticalFlip}>
                            <CgMergeVertical className="icon" />
                        </div>
                        <div className="icon-container" onClick={horizentalFlip}>
                            <CgMergeHorizontal className="icon" />
                        </div>
                        <div className="icon-container" onClick={undo}>
                            <IoMdUndo className="icon" />
                        </div>
                        <div className="icon-container" onClick={redo}>
                            <IoMdRedo className="icon" />
                        </div>
                        <div className="icon-container" onClick={() => setDrawingEnabled(!drawingEnabled)}>
                            <GrEdit className="icon" />
                        </div>
                        <div className="icon-container" onClick={() => setEditingText(!editingText)}>
                            <GrSuperscript className="icon" />
                        </div>
                        <div className="icon-container" onClick={imageCrop}>
                            <GrDown className="icon" />
                        </div>
                        <div className="icon-container" onClick={saveImage}>
                            <IoIosSave className="icon" />
                        </div>
                    </div>
                </div>
                <div className="canvas-container">
                    <canvas
                        ref={canvasRef}
                        className="canvas"
                        onMouseDown={handleMouseDown}
                        onMouseUp={handleMouseUp}
                        onMouseMove={handleMouseMove}
                    ></canvas>
                    {editingText && (
                        <input
                            type="text"
                            value={text.value}
                            onChange={handleTextChange}
                            onMouseDown={handleTextMouseDown}
                            style={{
                                position: 'absolute',
                                left: `${text.x}px`,
                                top: `${text.y}px`,
                                border: 'none',
                                background: 'transparent',
                                color: 'red',
                                fontSize: '30px',
                                outline: 'none'
                            }}
                        />
                    )}
                </div>
            </div>
        </div>
    );
};

export default Main;
