import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosImage } from 'react-icons/io';
import storeData from './LinkedList';
import { Stage, Layer, Image, Line } from 'react-konva';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];
    
    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [lines, setLines] = useState([]);
    const [currentLine, setCurrentLine] = useState([]);
    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    useEffect(() => {
        if (details) {
            // Handle image filters and transformations
        }
    }, [state, details, crop]);

    const handleImageLoad = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const handleMouseDown = (e) => {
        if (drawing) {
            const { x, y } = e.target.getStage().getPointerPosition();
            setCurrentLine([{ x, y }]);
        }
    };

    const handleMouseMove = (e) => {
        if (drawing && currentLine.length > 0) {
            const { x, y } = e.target.getStage().getPointerPosition();
            setCurrentLine([...currentLine, { x, y }]);
        }
    };

    const handleMouseUp = () => {
        if (drawing && currentLine.length > 0) {
            setLines([...lines, { points: currentLine.flat() }]);
            setCurrentLine([]);
        }
    };

    const pencilTool = () => {
        setDrawing(!drawing);
    };

    const drawImage = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        if (!details) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
        
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );

        return canvas.toDataURL();
    };

    const saveImage = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        if (!details) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
        
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );

        // Draw pencil lines
        lines.forEach(line => {
            ctx.beginPath();
            ctx.moveTo(line.points[0], line.points[1]);
            for (let i = 2; i < line.points.length; i += 2) {
                ctx.lineTo(line.points[i], line.points[i + 1]);
            }
            ctx.lineWidth = 2;
            ctx.strokeStyle = 'red'; // Pencil color
            ctx.stroke();
        });

        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    return (
        <div className='image_editor'>
            <div className="card">
                <div className="card_header">
                    <h2>------ Image Editor ------</h2>
                </div>
                <div className="card_body">
                    <div className="sidebar">
                        <div className="side_body">
                            <div className="filter_section">
                                <span>Filters</span>
                                <div className="filter_key">
                                    {filterElement.map((v, i) => (
                                        <button
                                            className={property.name === v.name ? 'active' : ''}
                                            onClick={() => setProperty(v)}
                                            key={i}
                                        >
                                            {v.name}
                                        </button>
                                    ))}
                                </div>
                            </div>
                            <div className="filter_slider">
                                <div className="label_bar">
                                    <label htmlFor="range">{property.name.charAt(0).toUpperCase() + property.name.slice(1)}</label>
                                    <span>{state[property.name]}</span>
                                </div>
                                <input
                                    name={property.name}
                                    onChange={e => setState({ ...state, [e.target.name]: e.target.value })}
                                    value={state[property.name]}
                                    max={property.maxValue}
                                    type="range"
                                />
                            </div>
                            <div className="rotate">
                                <label htmlFor="">Rotate & Flip</label>
                                <div className="icon">
                                    <div onClick={() => setState(prev => ({ ...prev, rotate: prev.rotate - 90 }))}><GrRotateLeft /></div>
                                    <div onClick={() => setState(prev => ({ ...prev, rotate: prev.rotate + 90 }))}><GrRotateRight /></div>
                                    <div onClick={() => setState(prev => ({ ...prev, vartical: prev.vartical === 1 ? -1 : 1 }))}><CgMergeVertical /></div>
                                    <div onClick={() => setState(prev => ({ ...prev, horizental: prev.horizental === 1 ? -1 : 1 }))}><CgMergeHorizontal /></div>
                                </div>
                                <div className="icon">
                                    <div onClick={pencilTool} className={drawing ? 'active' : ''}><GrEdit /></div>
                                </div>
                            </div>
                        </div>
                        <div className="reset">
                            <button onClick={() => setState({
                                image: '',
                                brightness: 100,
                                grayscale: 0,
                                sepia: 0,
                                saturate: 100,
                                contrast: 100,
                                hueRotate: 0,
                                rotate: 0,
                                vartical: 1,
                                horizental: 1
                            })}>Reset</button>
                            <button onClick={saveImage} className='save'>Save Image</button>
                        </div>
                    </div>
                    <div className="image_section">
                        <div className="image">
                            {state.image ? (
                                <Stage
                                    width={window.innerWidth}
                                    height={window.innerHeight}
                                    onMouseDown={handleMouseDown}
                                    onMouseMove={handleMouseMove}
                                    onMouseUp={handleMouseUp}
                                >
                                    <Layer>
                                        <Image
                                            image={details}
                                            width={window.innerWidth}
                                            height={window.innerHeight}
                                            filter={Konva.Filters.Blur}
                                            blurRadius={state.blur}
                                            rotation={state.rotate}
                                            scaleX={state.vartical}
                                            scaleY={state.horizental}
                                        />
                                        {lines.map((line, i) => (
                                            <Line
                                                key={i}
                                                points={line.points}
                                                stroke="red"
                                                strokeWidth={2}
                                                lineCap="round"
                                                lineJoin="round"
                                            />
                                        ))}
                                        {currentLine.length > 0 && (
                                            <Line
                                                points={currentLine.flat()}
                                                stroke="red"
                                                strokeWidth={2}
                                                lineCap="round"
                                                lineJoin="round"
                                            />
                                        )}
                                    </Layer>
                                </Stage>
                            ) : (
                                <label htmlFor="choose">
                                    <IoIosImage />
                                    <span>Choose Image</span>
                                </label>
                            )}
                        </div>
                        <div className="image_select">
                            <button onClick={undo} className='undo'><IoMdUndo /></button>
                            <button onClick={redo} className='redo'><IoMdRedo /></button>
                            {crop && <button onClick={imageCrop} className='crop'>Crop Image</button>}
                            <label htmlFor="choose">Choose Image</label>
                            <input onChange={handleImageLoad} type="file" id='choose' />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
