import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
// import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit, GrSuperscript, GrDown } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosSave, IoIosImage } from 'react-icons/io';
import storeData from './LinkedList';
import logo from './style/logo.png';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];

    const [showDropdown, setShowDropdown] = useState(false);
    const toggleDropdown = () => setShowDropdown(!showDropdown);
    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    // const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [drawingEnabled, setDrawingEnabled] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50 });
    const [editingText, setEditingText] = useState(false);
    const canvasRef = useRef(null);

    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    useEffect(() => {
        const screenshotDataUrl = localStorage.getItem("screenshotDataUrl");
        if (screenshotDataUrl) {
            const img = new Image();
            img.src = screenshotDataUrl;
            img.onload = () => {
                setDetails(img);
                setState((prevState) => ({
                    ...prevState,
                    image: screenshotDataUrl
                }));
                storeData.insert({
                    image: screenshotDataUrl,
                    brightness: 100,
                    grayscale: 0,
                    sepia: 0,
                    saturate: 100,
                    contrast: 100,
                    hueRotate: 0,
                    rotate: 0,
                    vartical: 1,
                    horizental: 1
                });
            };
        }
    }, []);

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;
    
        // Adjust the canvas size to match the image size
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
    
        // Clear the canvas before drawing
        ctx.clearRect(0, 0, canvas.width, canvas.height);
    
        // Apply filters and transformations
        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -canvas.width / 2, -canvas.height / 2, canvas.width, canvas.height);
        ctx.resetTransform();
    
        // Draw the text if any
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
        }
    };

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const croppedImage = canvas.toDataURL('image/jpeg');
        const img = new Image();
        img.src = croppedImage;
        img.onload = () => {
            setDetails(img);
            setState({
                ...state,
                image: croppedImage
            });
            storeData.insert({
                ...state,
                image: croppedImage
            });
        };
    };

    const saveImage = () => {
        const link = document.createElement('a');
        link.download = 'image-edit.jpg';
        link.href = canvasRef.current.toDataURL();
        link.click();
    };

    const enableDrawing = () => {
        setDrawingEnabled(true);
    };

    const handleMouseDown = (e) => {
        if (!drawingEnabled) return;

        setDrawing(true);
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.beginPath();
        ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
    };

    const handleMouseMove = (e) => {
        if (!drawing || !drawingEnabled) return;

        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        ctx.stroke();
    };

    const handleMouseUp = () => {
        setDrawing(false);
        setDrawingEnabled(false);
    };

    const handleTextClick = (e) => {
        if (editingText) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.font = '30px Arial';
            ctx.fillStyle = 'red';
            ctx.fillText(text.value, text.x, text.y);
            setEditingText(false);
        } else {
            setEditingText(true);
        }
    };
 
    const handleShapeSelect = (e) =>{
        
    }
    const handleTextChange = (e) => {
        setText({ ...text, value: e.target.value });
    };

    const handleTextMove = (e) => {
        if (editingText) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            drawImage();
            setText({ ...text, x: e.nativeEvent.offsetX, y: e.nativeEvent.offsetY });
        }
    };

    return (
        <div className="container">
            <header className="header">
                <div className="logo">
                    <img src={logo} alt="Logo" />
                </div>
                <div className="nav">
                    <div className="inputImage">
                        <input type="file" id="file" onChange={imageHandle} />
                        <label htmlFor="file">
                            <IoIosImage />
                        </label>
                    </div>
                    <button onClick={saveImage}>
                        <IoIosSave />
                    </button>
                </div>
            </header>

            <div className="app">
                <aside className="app-sidebar">
                    <button onClick={undo}>
                        <IoMdUndo />
                    </button>
                    <button onClick={redo}>
                        <IoMdRedo />
                    </button>
                    <button onClick={leftRotate}>
                        <GrRotateLeft />
                    </button>
                    <button onClick={rightRotate}>
                        <GrRotateRight />
                    </button>
                    <button onClick={varticalFlip}>
                        <CgMergeVertical />
                    </button>
                    <button onClick={horizentalFlip}>
                        <CgMergeHorizontal />
                    </button>
                    <button onClick={imageCrop}>
                        <GrSuperscript />
                    </button>
                    <div className="slider">
                        <input
                            type="range"
                            name={property.name}
                            max={property.maxValue}
                            value={state[property.name]}
                            onChange={inputHandle}
                        />
                    </div>
                    <button onClick={toggleDropdown}>
                        <GrDown />
                    </button>
                    {showDropdown && (
                        <div className="dropdown">
                            {filterElement.map((v, i) => (
                                <button key={i} onClick={() => setProperty(v)}>
                                    {v.name}
                                </button>
                            ))}
                        </div>
                    )}
                    <button onClick={enableDrawing}>
                        <GrEdit />
                    </button>
                    <button onClick={() => handleShapeSelect('circle')}>
                        Circle
                    </button>
                    <button onClick={() => handleShapeSelect('rectangle')}>
                        Rectangle
                    </button>
                </aside>

                <main className="app-content">
                    {state.image && (
                        <div className="canvas-container">
                            <canvas
                                className="responsive-canvas"
                                ref={canvasRef}
                                onMouseDown={handleMouseDown}
                                onMouseMove={handleMouseMove}
                                onMouseUp={handleMouseUp}
                                onClick={handleTextMove}
                            />
                            {editingText && (
                                <input
                                    type="text"
                                    value={text.value}
                                    onChange={handleTextChange}
                                    onClick={handleTextClick}
                                    style={{ left: text.x, top: text.y }}
                                />
                            )}
                        </div>
                    )}
                </main>
            </div>
        </div>
    );
};

export default Main;
