import React, { useState, useRef, useEffect } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit, GrSuperscript } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosSave, IoIosImage } from 'react-icons/io';
import { SketchPicker } from 'react-color';
import storeData from './LinkedList';
import logo from './style/logo.png';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 100 },
        { name: 'sepia', maxValue: 100 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate', maxValue: 360 }
    ];

    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState(null);
    const [crop, setCrop] = useState({});
    const [drawing, setDrawing] = useState(false);
    const [drawingEnabled, setDrawingEnabled] = useState(false);
    const [text, setText] = useState({ value: '', x: 50, y: 50, color: 'red' });
    const [editingText, setEditingText] = useState(false);
    const [textColor, setTextColor] = useState('#FF0000'); // Initial color red
    const canvasRef = useRef(null);

    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });

    useEffect(() => {
        if (details) {
            drawImage();
        }
    }, [state, details, crop, text]);

    const drawImage = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        if (!details || !canvas) return;

        const { width, height } = canvas;

        // Adjust the canvas size to match the image size
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        ctx.clearRect(0, 0, width, height);

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(width / 2, height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(details, -width / 2, -height / 2, width, height);
        ctx.resetTransform();

        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = text.color; // Use the selected text color
            ctx.fillText(text.value, text.x, text.y);
        }
    };

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate - 90
        }));
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState(prevState => ({
            ...prevState,
            rotate: prevState.rotate + 90
        }));
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState(prevState => ({
            ...prevState,
            vartical: prevState.vartical === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState(prevState => ({
            ...prevState,
            horizental: prevState.horizental === 1 ? -1 : 1
        }));
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                const img = new Image();
                img.src = reader.result;
                img.onload = () => {
                    setDetails(img);
                    setState({
                        ...state,
                        image: reader.result
                    });
                    storeData.insert({
                        image: reader.result,
                        brightness: 100,
                        grayscale: 0,
                        sepia: 0,
                        saturate: 100,
                        contrast: 100,
                        hueRotate: 0,
                        rotate: 0,
                        vartical: 1,
                        horizental: 1
                    });
                };
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        if (!details || !crop || !canvasRef.current) return;

        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;

        canvas.width = crop.width;
        canvas.height = crop.height;

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpeg');
        setState({
            ...state,
            image: base64Url
        });
    };

    const saveImage = () => {
        const canvas = document.createElement('canvas');
        const ctx = canvas.getContext('2d');

        if (!details) return;

        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;

        ctx.filter = `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);
        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );
        ctx.resetTransform();

        // Draw the text
        if (text.value) {
            ctx.font = '30px Arial';
            ctx.fillStyle = text.color; // Use the selected text color
            ctx.fillText(text.value, text.x, text.y);
        }

        // Draw the current content of the canvas (including lines)
        const currentCanvas = canvasRef.current;
        if (currentCanvas) {
            ctx.drawImage(currentCanvas, 0, 0);
        }

        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    const startDrawing = (e) => {
        if (drawingEnabled) {
            setDrawing(true);
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };

    const draw = (e) => {
        if (drawing && drawingEnabled) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.strokeStyle = '#000000'; // Set the pencil color (black)
            ctx.lineWidth = 2; // Set the pencil width
            ctx.lineCap = 'round'; // Set the line cap for smoother lines
            ctx.stroke();
        }
    };

    const stopDrawing = () => {
        if (drawing) {
            setDrawing(false);
        }
    };

    const enableDrawing = () => {
        setDrawingEnabled(!drawingEnabled);
    };

    const handleTextClick = (e) => {
        const rect = e.target.getBoundingClientRect();
        setText({ ...text, x: e.clientX - rect.left, y: e.clientY - rect.top });
    };

    const handleTextChange = (e) => {
        setText({ ...text, value: e.target.value });
    };

    const handleColorChange = (color) => {
        setTextColor(color.hex);
        setText({ ...text, color: color.hex });
    };

    const handleTextEdit = () => {
        setEditingText(!editingText);
    };

    return (
        <div className='container'>
            <div className="app">
                <div className="app__header">
                    <div className="app__header-logo">
                        <img src={logo} alt="Logo" />
                    </div>
                    <div className="app__header-buttons">
                        <button className="app__header-save" onClick={saveImage}><IoIosSave /></button>
                    </div>
                </div>
                <div className="app__content">
                    {state.image ? (
                        <div className="image-canvas">
                            <canvas
                                ref={canvasRef}
                                className="image-canvas__canvas"
                                onMouseDown={startDrawing}
                                onMouseMove={draw}
                                onMouseUp={stopDrawing}
                                onClick={handleTextClick}
                            />
                            <input
                                type="text"
                                value={text.value}
                                onChange={handleTextChange}
                                onClick={handleTextEdit}
                                className="image-canvas__text-input"
                                style={{
                                    top: text.y,
                                    left: text.x,
                                    position: 'absolute',
                                    color: text.color,
                                    display: editingText ? 'block' : 'none'
                                }}
                            />
                        </div>
                    ) : (
                        <div className="app__upload">
                            <label htmlFor="image-upload">
                                <IoIosImage />
                                <span>Upload Image</span>
                            </label>
                            <input id="image-upload" type="file" accept="image/*" onChange={imageHandle} />
                        </div>
                    )}
                </div>
                <div className="app__sidebar">
                    <div className="sidebar__filter">
                        <p>Filter</p>
                        <ul>
                            {filterElement.map((v, i) => (
                                <li key={i}>
                                    <button onClick={() => setProperty(v)}>{v.name}</button>
                                </li>
                            ))}
                        </ul>
                        <div className="sidebar__filter--slider">
                            <input
                                type="range"
                                name={property.name}
                                max={property.maxValue}
                                value={state[property.name]}
                                onChange={inputHandle}
                            />
                        </div>
                    </div>
                    <div className="sidebar__rotate">
                        <p>Rotate</p>
                        <div className="button">
                            <button onClick={leftRotate}><GrRotateLeft /></button>
                            <button onClick={rightRotate}><GrRotateRight /></button>
                        </div>
                    </div>
                    <div className="sidebar__flip">
                        <p>Flip</p>
                        <div className="button">
                            <button onClick={varticalFlip}><CgMergeVertical /></button>
                            <button onClick={horizentalFlip}><CgMergeHorizontal /></button>
                        </div>
                    </div>
                    <div className="sidebar__edit">
                        <p>Edit</p>
                        <div className="button">
                            <button onClick={undo}><IoMdUndo /></button>
                            <button onClick={redo}><IoMdRedo /></button>
                        </div>
                    </div>
                    <div className="sidebar__draw">
                        <p>Draw</p>
                        <div className="button">
                            <button onClick={enableDrawing}><GrEdit /></button>
                        </div>
                    </div>
                    <div className="sidebar__text">
                        <p>Text</p>
                        <div className="button">
                            <button onClick={handleTextEdit}><GrSuperscript /></button>
                        </div>
                        {editingText && (
                            <SketchPicker
                                color={textColor}
                                onChange={handleColorChange}
                            />
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
