import React, { useState, useRef } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight, GrEdit } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosImage } from 'react-icons/io';
import storeData from './LinkedList';

const filterElement = [
    { name: 'brightness', maxValue: 200 },
    { name: 'grayscale', maxValue: 100 },
    { name: 'sepia', maxValue: 100 },
    { name: 'saturate', maxValue: 200 },
    { name: 'contrast', maxValue: 200 },
    { name: 'hueRotate', maxValue: 360 }
];
const Main = () => {
    const [property, setProperty] = useState({
        name: 'brightness',
        maxValue: 200,
    });
    const [details, setDetails] = useState('');
    const [crop, setCrop] = useState('');
    const [drawing, setDrawing] = useState(false);
    const canvasRef = useRef(null);
    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1,
    });

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value,
        });
    };

    const leftRotate = () => {
        setState({
            ...state,
            rotate: state.rotate - 90,
        });

        const stateData = state;
        stateData.rotate = state.rotate - 90;
        storeData.insert(stateData);
    };

    const rightRotate = () => {
        setState({
            ...state,
            rotate: state.rotate + 90,
        });
        const stateData = state;
        stateData.rotate = state.rotate + 90;
        storeData.insert(stateData);
    };

    const varticalFlip = () => {
        setState({
            ...state,
            vartical: state.vartical === 1 ? -1 : 1,
        });
        const stateData = state;
        stateData.vartical = state.vartical === 1 ? -1 : 1;
        storeData.insert(stateData);
    };

    const horizentalFlip = () => {
        setState({
            ...state,
            horizental: state.horizental === 1 ? -1 : 1,
        });
        const stateData = state;
        stateData.horizental = state.horizental === 1 ? -1 : 1;
        storeData.insert(stateData);
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) {
            setState(data);
        }
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) {
            setState(data);
        }
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();

            reader.onload = () => {
                setState({
                    ...state,
                    image: reader.result,
                });

                const stateData = {
                    image: reader.result,
                    brightness: 100,
                    grayscale: 0,
                    sepia: 0,
                    saturate: 100,
                    contrast: 100,
                    hueRotate: 0,
                    rotate: 0,
                    vartical: 1,
                    horizental: 1,
                };
                storeData.insert(stateData);
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const imageCrop = () => {
        const canvas = document.createElement('canvas');
        const scaleX = details.naturalWidth / details.width;
        const scaleY = details.naturalHeight / details.height;
        canvas.width = crop.width;
        canvas.height = crop.height;
        const ctx = canvas.getContext('2d');

        ctx.drawImage(
            details,
            crop.x * scaleX,
            crop.y * scaleY,
            crop.width * scaleX,
            crop.height * scaleY,
            0,
            0,
            crop.width,
            crop.height
        );

        const base64Url = canvas.toDataURL('image/jpg');

        setState({
            ...state,
            image: base64Url,
        });
    };

    const saveImage = () => {
        const canvas = canvasRef.current;
        canvas.width = details.naturalWidth;
        canvas.height = details.naturalHeight;
        const ctx = canvas.getContext('2d');

        ctx.filter = `brightness(${state.brightness}%) brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`;

        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(state.rotate * Math.PI / 180);
        ctx.scale(state.vartical, state.horizental);

        ctx.drawImage(
            details,
            -canvas.width / 2,
            -canvas.height / 2,
            canvas.width,
            canvas.height
        );

        const link = document.createElement('a');
        link.download = 'image_edit.jpg';
        link.href = canvas.toDataURL();
        link.click();
    };

    const startDrawing = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.beginPath();
            ctx.moveTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
        }
    };

    const draw = (e) => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.lineTo(e.nativeEvent.offsetX, e.nativeEvent.offsetY);
            ctx.stroke();
        }
    };

    const stopDrawing = () => {
        if (drawing) {
            const canvas = canvasRef.current;
            const ctx = canvas.getContext('2d');
            ctx.closePath();
        }
    };

    const enableDrawing = () => {
        setDrawing(!drawing);
    };

    return (
        <div className='image_editor'>
            <div className="card">
                <div className="card_header">
                    <h2>------ Image Editor ------</h2>
                </div>
                <div className="card_body">
                    <div className="sidebar">
                        <div className="side_body">
                            <div className="filter_section">
                                <span>Filters</span>
                                <div className="filter_key">
                                    {filterElement.map((v, i) => (
                                        <button
                                            className={property.name === v.name ? 'active' : ''}
                                            onClick={() => setProperty(v)}
                                            key={i}
                                        >
                                            {v.name}
                                        </button>
                                    ))}
                                </div>
                            </div>
                            <div className="filter_slider">
                                <div className="label_bar">
                                    <label htmlFor="range">Rotate</label>
                                    <span>100%</span>
                                </div>
                                <input
                                    name={property.name}
                                    onChange={inputHandle}
                                    value={state[property.name]}
                                    max={property.maxValue}
                                    type="range"
                                />
                            </div>
                            <div className="rotate">
                                <label htmlFor="">Rotate & Flip</label>
                                <div className="icon">
                                    <div onClick={leftRotate}>
                                        <GrRotateLeft />
                                    </div>
                                    <div onClick={rightRotate}>
                                        <GrRotateRight />
                                    </div>
                                    <div onClick={varticalFlip}>
                                        <CgMergeVertical />
                                    </div>
                                    <div onClick={horizentalFlip}>
                                        <CgMergeHorizontal />
                                    </div>
                                </div>
                            </div>
                            <div className="rotate">
                                <label htmlFor="">Editor icons</label>
                                <div className="icon">
                                    <div onClick={enableDrawing}>
                                        <GrEdit />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="reset">
                            <button>Reset</button>
                            <button onClick={saveImage} className='save'>
                                Save Image
                            </button>
                        </div>
                    </div>
                    <div className="image_section">
                        <div className="image">
                            {state.image ? (
                                <ReactCrop crop={crop} onChange={(c) => setCrop(c)}>
                                    <canvas
                                        ref={canvasRef}
                                        onMouseDown={startDrawing}
                                        onMouseMove={draw}
                                        onMouseUp={stopDrawing}
                                        style={{
                                            filter: `brightness(${state.brightness}%) brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`,
                                            transform: `rotate(${state.rotate}deg) scale(${state.vartical},${state.horizental})`,
                                        }}
                                        src={state.image}
                                        alt=""
                                    />
                                </ReactCrop>
                            ) : (
                                ''
                            )}
                        </div>
                        <div className="slider">
                            <div className="label_bar">
                                <label htmlFor="range">Rotate</label>
                                <span>100%</span>
                            </div>
                            <input
                                name={property.name}
                                onChange={inputHandle}
                                value={state[property.name]}
                                max={property.maxValue}
                                type="range"
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
