import React, { useState } from 'react';
import './style/main.scss';
import ReactCrop from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';
import { GrRotateLeft, GrRotateRight } from 'react-icons/gr';
import { CgMergeVertical, CgMergeHorizontal } from 'react-icons/cg';
import { IoMdUndo, IoMdRedo, IoIosImage } from 'react-icons/io';
import { FaPencilAlt, FaRegCircle } from 'react-icons/fa';
import { Stage, Layer, Line, Circle, Image } from 'react-konva';
import storeData from './LinkedList';
import useImage from 'use-image';

const Main = () => {
    const filterElement = [
        { name: 'brightness', maxValue: 200 },
        { name: 'grayscale', maxValue: 200 },
        { name: 'sepia', maxValue: 200 },
        { name: 'saturate', maxValue: 200 },
        { name: 'contrast', maxValue: 200 },
        { name: 'hueRotate' }
    ];

    const [property, setProperty] = useState({ name: 'brightness', maxValue: 200 });
    const [details, setDetails] = useState('');
    const [crop, setCrop] = useState('');
    const [state, setState] = useState({
        image: '',
        brightness: 100,
        grayscale: 0,
        sepia: 0,
        saturate: 100,
        contrast: 100,
        hueRotate: 0,
        rotate: 0,
        vartical: 1,
        horizental: 1
    });
    const [drawing, setDrawing] = useState(false);
    const [lines, setLines] = useState([]);
    const [circles, setCircles] = useState([]);
    const [tool, setTool] = useState('');

    const inputHandle = (e) => {
        setState({
            ...state,
            [e.target.name]: e.target.value
        });
    };

    const leftRotate = () => {
        setState({ ...state, rotate: state.rotate - 90 });
        storeData.insert({ ...state, rotate: state.rotate - 90 });
    };

    const rightRotate = () => {
        setState({ ...state, rotate: state.rotate + 90 });
        storeData.insert({ ...state, rotate: state.rotate + 90 });
    };

    const varticalFlip = () => {
        setState({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
        storeData.insert({ ...state, vartical: state.vartical === 1 ? -1 : 1 });
    };

    const horizentalFlip = () => {
        setState({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
        storeData.insert({ ...state, horizental: state.horizental === 1 ? -1 : 1 });
    };

    const redo = () => {
        const data = storeData.redoEdit();
        if (data) setState(data);
    };

    const undo = () => {
        const data = storeData.undoEdit();
        if (data) setState(data);
    };

    const imageHandle = (e) => {
        if (e.target.files.length !== 0) {
            const reader = new FileReader();
            reader.onload = () => {
                setState({ ...state, image: reader.result });
                storeData.insert({
                    image: reader.result,
                    brightness: 100,
                    grayscale: 0,
                    sepia: 0,
                    saturate: 100,
                    contrast: 100,
                    hueRotate: 0,
                    rotate: 0,
                    vartical: 1,
                    horizental: 1
                });
            };
            reader.readAsDataURL(e.target.files[0]);
        }
    };

    const handleMouseDown = (e) => {
        if (tool === 'pencil') {
            setDrawing(true);
            const pos = e.target.getStage().getPointerPosition();
            setLines([...lines, { points: [pos.x, pos.y] }]);
        } else if (tool === 'circle') {
            const pos = e.target.getStage().getPointerPosition();
            setCircles([...circles, { x: pos.x, y: pos.y, radius: 50 }]);
        }
    };

    const handleMouseMove = (e) => {
        if (!drawing) return;
        const stage = e.target.getStage();
        const point = stage.getPointerPosition();
        let lastLine = lines[lines.length - 1];
        lastLine.points = lastLine.points.concat([point.x, point.y]);
        lines.splice(lines.length - 1, 1, lastLine);
        setLines(lines.concat());
    };

    const handleMouseUp = () => setDrawing(false);

    return (
        <div className='image_editor'>
            <div className="card">
                <div className="card_header">
                    <h2>------ Image Editor ------</h2>
                </div>
                <div className="card_body">
                    <div className="sidebar">
                        <div className="side_body">
                            <div className="filter_section">
                                <span>Filters</span>
                                <div className="filter_key">
                                    {filterElement.map((v, i) => (
                                        <button
                                            className={property.name === v.name ? 'active' : ''}
                                            onClick={() => setProperty(v)}
                                            key={i}
                                        >
                                            {v.name}
                                        </button>
                                    ))}
                                </div>
                            </div>
                            <div className="filter_slider">
                                <div className="label_bar">
                                    <label htmlFor="range">Rotate</label>
                                    <span>100%</span>
                                </div>
                                <input
                                    name={property.name}
                                    onChange={inputHandle}
                                    value={state[property.name]}
                                    max={property.maxValue}
                                    type="range"
                                />
                            </div>
                            <div className="rotate">
                                <label htmlFor="">Rotate & Flip</label>
                                <div className="icon">
                                    <div onClick={leftRotate}><GrRotateLeft /></div>
                                    <div onClick={rightRotate}><GrRotateRight /></div>
                                    <div onClick={varticalFlip}><CgMergeVertical /></div>
                                    <div onClick={horizentalFlip}><CgMergeHorizontal /></div>
                                </div>
                            </div>
                            <div className="rotate">
                                <label htmlFor="">Editor icons</label>
                                <div className="icon">
                                    <div onClick={() => setTool('pencil')}><FaPencilAlt /></div>
                                    <div onClick={() => setTool('circle')}><FaRegCircle /></div>
                                </div>
                            </div>
                        </div>
                        <div className="reset">
                            <button>Reset</button>
                            <button className='save'>Save Image</button>
                        </div>
                    </div>
                    <div className="image_section">
                        <Stage
                            width={window.innerWidth}
                            height={window.innerHeight}
                            onMouseDown={handleMouseDown}
                            onMousemove={handleMouseMove}
                            onMouseup={handleMouseUp}
                        >
                            <Layer>
                                {state.image && (
                                    <Image
                                        image={useImage(state.image)[0]}
                                        style={{ filter: `brightness(${state.brightness}%) sepia(${state.sepia}%) saturate(${state.saturate}%) contrast(${state.contrast}%) grayscale(${state.grayscale}%) hue-rotate(${state.hueRotate}deg)`, transform: `rotate(${state.rotate}deg) scale(${state.vartical},${state.horizental})` }}
                                    />
                                )}
                                {lines.map((line, i) => (
                                    <Line key={i} points={line.points} stroke="black" strokeWidth={2} />
                                ))}
                                {circles.map((circle, i) => (
                                    <Circle key={i} x={circle.x} y={circle.y} radius={circle.radius} stroke="black" draggable />
                                ))}
                            </Layer>
                        </Stage>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Main;
